INSERT INTO foods (id, state_id, name, description, image_url) VALUES
(30, 12, 'Chaas', 'Chaas, also known as buttermilk, is a popular drink in Haryana, particularly during the hot summer months. It is made from yogurt and is often flavored with cumin, salt, and black pepper. It is known for its cooling properties and digestive benefits.', '../Image/Haryana/Chaas.jpeg'),
(31, 12, 'Paratha', 'Paratha is a common breakfast or lunch dish in Haryana. It is a flatbread made of whole wheat flour, often stuffed with vegetables like potatoes, cauliflower, or radish. It is served with yogurt, pickle, and a dollop of ghee.', '../Image/Haryana/Paratha.webp'),
(32, 12, 'Bajra Khichdi', 'A hearty dish made from millet (bajra) and moong dal (yellow lentils). This dish is commonly prepared during the winter months and is a staple food in Haryana, especially in rural areas. It is often paired with a dollop of ghee and served with buttermilk.', '../Image/Haryana/Bajra Khichdi.jpg');
