INSERT INTO foods (id, state_id, name, description, image_url) VALUES
(73, 28, 'Butter Chicken', 'Butter chicken, also known as Murgh Makhani, is a famous Punjabi dish consisting of chicken cooked in a rich and creamy tomato-based gravy. It is often paired with naan or rice and is a favorite among food lovers across the world.', '../Image/Punjab/ButterChicken.jpg'),
(74, 28, 'Chole Bhature', 'A traditional Punjabi dish, Chole Bhature consists of spicy chickpeas (chole) served with fried bread (bhature). It is a popular breakfast and lunch dish, often accompanied by pickles and yogurt.', '../Image/Chandigarh/CholeBhature.jpg'),
(75, 28, 'Amritsari Kulcha', 'Amritsari Kulcha is a popular stuffed flatbread from the city of Amritsar. The bread is filled with spiced mashed potatoes and baked in a tandoor, giving it a crispy exterior and soft interior. It is served with curd, pickles, and chole (spicy chickpeas).', '../Image/Punjab/Amritsari Kulche.avif');
INSERT INTO foods (id, state_id, name, description, image_url) VALUES
(76, 29, 'Dal Baati Churma', 'This iconic Rajasthani dish consists of dal (lentils), baati (wheat bread), and churma (a sweet, crushed wheat dish). It is a wholesome, flavorful meal enjoyed across Rajasthan, often served with ghee, pickles, and yogurt.', '../Image/Rajasthan/Dal Baati Churma.webp'),
(77, 29, 'Gatte ki Sabzi', 'A savory curry made with gram flour (besan) dumplings, this dish is a specialty of Rajasthan. The gattas are cooked in a spicy gravy, and it is typically served with roti or rice.', '../Image/Rajasthan/Gatte ki Sabzi.jpg'),
