INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(4, 2, 'Tirupati Temple', 'Tirupati is one of the most famous pilgrimage destinations in India, known for the Sri Venkateswara Temple. Devotees from all over the world visit to seek the blessings of Lord Venkateswara. The temple is situated atop the Tirumala hills, offering breathtaking views and a serene atmosphere.', '../Image/AndhraPradesh/Tirupati temple.avif'),
(5, 2, 'Araku Valley', 'Araku Valley is a scenic hill station located in the Eastern Ghats, known for its lush greenery, coffee plantations, and pleasant climate. The valley offers a perfect escape from the heat, with its picturesque landscapes and vibrant tribal culture. It is a popular destination for trekking and nature walks.', '../Image/AndhraPradesh/Araku valley.jpg'),
(6, 2, 'Visakhapatnam Beaches', 'Visakhapatnam, also known as Vizag, is home to some of the most beautiful beaches in Andhra Pradesh. The beaches, such as Rushikonda and Yarada, offer stunning views of the coastline and are ideal for relaxation, beach sports, and water activities. Vizag is also known for its rich cultural heritage and the scenic beauty of the surrounding hills and coastline.', '../Image/AndhraPradesh/Visakhapatnam Beach.webp');
