INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(30, 12, 'Sultanpur Bird Sanctuary', 'Located near Gurgaon, Sultanpur Bird Sanctuary is a famous bird-watching destination in Haryana. The sanctuary is home to hundreds of species of migratory and resident birds, making it a popular spot for nature lovers and photographers.', '../Image/Haryana/Sultanpur Bird Sanctuary.jpg'),
(31, 12, 'Pinjore Gardens', 'Pinjore Gardens is a historical garden in the town of Pinjore. It was designed by the Mughal emperors and is known for its lush green landscapes, fountains, and scenic beauty. It is a popular tourist spot and is often visited by locals for picnics and leisure walks.', '../Image/Haryana/Pinjore Gardens.webp'),
(32, 12, 'Baba Farid Tomb', 'Baba Farid Tomb, located in Faridabad, is an important historical and spiritual site in Haryana. The tomb is dedicated to the famous Sufi saint Baba Farid and is visited by pilgrims and tourists alike for its tranquility and historical significance.', '../Image/Haryana/Baba Farid Tomb.webp');
INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(33, 13, 'Manali', 'Manali is a beautiful hill station located in the Kullu Valley. It is known for its adventure sports, scenic landscapes, and vibrant atmosphere. Popular activities in Manali include trekking, skiing, paragliding, and rafting. The town is also known for its temples, including the Hadimba Temple.', '../Image/HimachalPradesh/Manali.webp'),
(34, 13, 'Spiti Valley', 'Known as "Little Tibet," Spiti Valley is a remote desert mountain valley located in the north-eastern part of Himachal Pradesh. The valley is famous for its Buddhist monasteries, stunning landscapes, and trekking trails. The valley is also home to ancient villages, including Kibber and Tabo, and offers an offbeat destination for adventure and cultural exploration.', '../Image/HimachalPradesh/Spiti Valley.jpeg'),
(35, 13, 'Kullu', 'Known as the "Valley of Gods," Kullu is famous for its scenic beauty, apple orchards, and temples. It is a great destination for nature lovers and adventure seekers, with activities such as rafting, trekking, and camping.', '../Image/HimachalPradesh/Kullu.jpg');
INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(36, 14, 'Dal Lake', 'Located in Srinagar, Dal Lake is one of the most famous tourist destinations in Jammu & Kashmir, known for its beautiful houseboats, shikaras, and picturesque surroundings. It is often referred to as the "Jewel in the crown of Kashmir".', '../Image/JammuKhasmir/Dal Lake.jpg'),
(37, 14, 'Gulmarg', 'A popular hill station and skiing destination, Gulmarg is known for its lush green meadows, snow-covered peaks, and the famous Gulmarg Gondola, one of the highest cable cars in the world.', '../Image/JammuKhasmir/Gulmarg.avif'),
(38, 14, 'Amarnath Cave', 'Located at an altitude of 3,888 meters, this sacred cave is a significant pilgrimage site for Hindus. The Amarnath Yatra is held annually and attracts thousands of devotees seeking blessings from the ice Shiva Lingam.', '../Image/JammuKhasmir/Amarnath Cave.jpg');
