INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(48, 18, 'Pangong Lake', 'Pangong Lake is a stunning high-altitude lake, known for its crystal-clear waters and breathtaking beauty. The lake, located at an altitude of 4,350 meters, is famous for changing colors throughout the day and has been featured in several Bollywood films.', '../Image/Ladakh/Pangong Lake.jpg'),
(49, 18, 'Leh Palace', 'Built in the 17th century, the Leh Palace offers stunning views of the Leh town and the surrounding Himalayan ranges. This ancient structure is a fine example of Tibetan architecture and once served as the residence of the kings of Ladakh.', '../Image/Ladakh/Leh Palace.jpg'),
