INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
(90, 34, 'Har Ki Pauri', 'Located in Haridwar, Har Ki Pauri is a famous ghat on the banks of the Ganges River. It is a sacred place for Hindus and attracts thousands of pilgrims every year. The evening Ganga Aarti at this ghat is a major attraction for visitors.', '../Image/Uttarakhand/Har Ki Pauri.jpg'),
(91, 34, 'Valley of Flowers', 'The Valley of Flowers National Park is a UNESCO World Heritage site located in the western Himalayas. This enchanting valley is covered with a variety of wildflowers during the monsoon season, making it one of the most beautiful places to visit in Uttarakhand.', '../Image/Uttarakhand/Valley of Flowers.jpg'),
(92, 34, 'Rishikesh', 'Rishikesh, known as the ''Yoga Capital of the World,'' is a spiritual town located on the banks of the Ganges River. It is a major center for yoga and meditation, and it also attracts adventure seekers for activities like white-water rafting and trekking.', '../Image/Uttarakhand/Rishikesh.jpg');
INSERT INTO tourist_places (id, state_id, name, description, image_url) VALUES
