INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(4, 2, 'Ugadi', 'Ugadi marks the beginning of the New Year in Andhra Pradesh. It is celebrated with great enthusiasm, with people decorating their homes, wearing new clothes, and offering prayers. Special dishes, such as Ugadi pachadi, are prepared, symbolizing different emotions of life: sweet, sour, bitter, and salty.', '../Image/AndhraPradesh/Ugadi.jpg'),
(5, 2, 'Pongal', 'Pongal is a harvest festival celebrated to thank the Sun God for a good harvest. It is celebrated with much fervor in Andhra Pradesh, especially in rural areas. People cook "Pongal," a dish made from newly harvested rice, and offer it as a token of gratitude for the harvest. The festival is marked by colorful decorations, traditional dance, and music.', '../Image/AndhraPradesh/Pongal.jpg'),
(6, 2, 'Ratha Saptami', 'Ratha Saptami is a significant Hindu festival dedicated to the Sun God. It is believed to be the day when the Sun God begins his northward journey. Devotees celebrate the festival by performing special rituals, including taking holy dips in rivers, offering prayers, and observing fasting. The festival is also marked by processions and chariot rides.', '../Image/AndhraPradesh/Ratha saptami.webp');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
