INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(30, 12, 'Makar Sankranti', 'Makar Sankranti is one of the most celebrated festivals in Haryana, marking the arrival of the harvest season. People celebrate by flying kites, dancing, and enjoying festive foods like tilgul (sesame and jaggery sweets). The festival is celebrated with much enthusiasm and joy.', '../Image/Haryana/Makar Sankranti.png'),
(31, 12, 'Teej Festival', 'Teej is a traditional festival celebrated by women in Haryana, marking the arrival of monsoon. Women dress up in vibrant clothes, adorn themselves with jewelry, and participate in singing, dancing, and fasting for the well-being of their husbands.', '../Image/Haryana/Teej Festival.jpg'),
(32, 12, 'Holi', 'Holi, the festival of colors, is celebrated with great enthusiasm in Haryana. People come together to play with colors, share sweets, and celebrate the arrival of spring. The festival brings people of all ages together to celebrate joy, love, and unity.', '../Image/Haryana/Holi.webp');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(33, 13, 'Kullu Dussehra', 'A week-long festival celebrated in the Kullu Valley, marking the victory of good over evil. The festival is known for its cultural performances, processions of deities, and traditional rituals. It is one of the most important festivals in Himachal Pradesh.', '../Image/HimachalPradesh/KulluDussehra.jpg'),
(34, 13, 'Phulaich', 'A unique festival celebrated by the Kullu people, marking the arrival of spring. During Phulaich, people offer prayers, sing traditional songs, and engage in festive dances. The festival is associated with the blossoming of wildflowers in the region.', '../Image/HimachalPradesh/Phulaich.jpg'),
(35, 13, 'Malani Mela', 'Celebrated annually in the Malana village, this festival showcases the regions local culture, traditional music, and vibrant dances. Malana is known for its unique customs and practices, and the Malani Mela is an important part of their cultural heritage.', '../Image/HimachalPradesh/MalaniMela.webp');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(36, 14, 'Eid al-Fitr', 'Eid is one of the most celebrated festivals in Jammu & Kashmir, marking the end of Ramadan. It is a time for family gatherings, prayers, and feasting. The regions vibrant traditions make it a joyous occasion.', '../Image/JammuKhasmir/Eid.webp'),
(37, 14, 'Navratri', 'A major Hindu festival celebrated with devotion and zeal. In Jammu & Kashmir, the festival is marked by prayers, fasting, dance, and cultural events. The Katra region, where Vaishno Devi temple is located, is especially vibrant during this period.', '../Image/JammuKhasmir/Navratri.jpg'),
(38, 14, 'Lohri', 'A festival celebrated predominantly in Jammu, marking the end of winter and the beginning of the harvest season. The festivities include lighting bonfires, singing folk songs, and dancing.', '../Image/JammuKhasmir/Lohri.webp');
