INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(70, 27, 'Bastille Day', 'Celebrated on July 14th, it includes parades and cultural events reflecting Puducherrys French heritage. Ex-servicemen march in uniform through the streets carrying the French and Indian flags.', '../Image/Puducherry/BastilleDay.jpg'),
(71, 27, 'Diwali', 'The Festival of Lights is celebrated with grandeur in Puducherry, with firecrackers, sweets, and festive decorations adorning homes and streets.', '../Image/Puducherry/Diwali.jpg'),
(72, 27, 'Masi Magam', 'A significant Tamil festival observed with processions of deities from temples to the sea for a ceremonial bath, attracting large crowds and spiritual gatherings.', '../Image/Puducherry/MasiMagam.jpeg');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(73, 28, 'Baisakhi', 'Baisakhi is the harvest festival of Punjab, celebrated with much enthusiasm. It marks the beginning of the harvest season and is also significant in Sikhism as the formation of the Khalsa was on this day. People celebrate Baisakhi by dancing the Bhangra and Gidda, singing folk songs, and enjoying traditional food.', '../Image/Punjab/Baisakhi.jpeg'),
(74, 28, 'Diwali', 'Diwali, also known as the Festival of Lights, is widely celebrated in Punjab. The festival marks the victory of light over darkness and good over evil. People celebrate by decorating their homes with diyas (oil lamps), bursting fireworks, and enjoying sweets.', '../Image/Punjab/Diwali.webp'),
(75, 28, 'Lohri', 'Lohri is a popular Punjabi festival marking the end of winter and the beginning of the harvest season. It is celebrated by lighting bonfires, singing folk songs, and dancing to the rhythms of the dhol. Lohri is especially popular in rural areas.', '../Image/Punjab/Lohri.jpg');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(76, 29, 'Pushkar Fair', 'The Pushkar Fair is one of the largest and most famous animal fairs in the world. Held annually in the town of Pushkar, the fair features camel trading, folk music and dance, cultural performances, and a lively atmosphere. It is a major cultural event in Rajasthan.', '../Image/Rajasthan/Pushkar Fair.jpg'),
(77, 29, 'Diwali', 'Diwali, the festival of lights, is celebrated with much fanfare in Rajasthan. Homes are decorated with oil lamps and colorful rangolis, and people celebrate by exchanging sweets, bursting crackers, and offering prayers to Goddess Lakshmi for wealth and prosperity.', '../Image/Rajasthan/Diwali.jpg'),
(78, 29, 'Teeyan', 'Teeyan is a festival celebrated by women in Rajasthan, especially in the monsoon season. Women dress in colorful attire, sing traditional songs, and celebrate with dancing and festive foods. It is a celebration of womanhood and the arrival of the monsoon rains.', '../Image/Rajasthan/Teeyan.jpg');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(79, 30, 'Losoong Festival', 'Celebrated as the Sikkimese New Year, Losoong marks the end of the harvest season and is observed with traditional dances and rituals.', '../Image/Sikkim/Losoong Festival.png'),
(80, 30, 'Maghe Sankranti', 'A major Hindu festival celebrated in Sikkim with holy dips, prayers, and feasting, marking the transition of the sun.', '../Image/Sikkim/Maghe Sankranti.png');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
