INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
(90, 34, 'Kumbh Mela', 'The Kumbh Mela is one of the largest religious gatherings in the world, held in Haridwar, Uttarakhand. Millions of devotees gather to take a holy dip in the Ganges River. The Kumbh Mela occurs once every 12 years and is celebrated with much fanfare, with devotees taking part in rituals and prayers.', '../Image/Uttarakhand/KumbhMela.jpg'),
(91, 34, 'Makar Sankranti', 'Makar Sankranti is a festival that marks the transition of the sun into the zodiac sign of Capricorn. In Uttarakhand, this festival is celebrated with kite flying, bonfires, and various cultural performances. It is one of the most popular festivals in the state.', '../Image/Uttarakhand/Makar Sankranti.jpg'),
(92, 34, 'Tulsi Vivah', 'Tulsi Vivah is an important Hindu festival celebrated in Uttarakhand. It marks the ceremonial marriage of the Tulsi plant to Lord Vishnu. The festival is celebrated with great devotion, especially in rural areas, where people offer prayers and perform rituals in temples and homes.', '../Image/Uttarakhand/Tulsi Vivah.jpg');
INSERT INTO festivals (id, state_id, name, description, image_url) VALUES
