CREATE TABLE states (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    name VARCHAR(255),
    capital VARCHAR(255),
    population VARCHAR(255),
    language VARCHAR(255),
    area VARCHAR(255),
    established VARCHAR(255),
    about VARCHAR(2000),
    about_capital VARCHAR(2000)
);




CREATE TABLE foods (
    id BIGINT PRIMARY KEY,
    state_id BIGINT,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    image_url VARCHAR(255),
    FOREIGN KEY (state_id) REFERENCES states(id) ON DELETE CASCADE
);







































CREATE TABLE tourist_places (
    id BIGINT PRIMARY KEY,
    state_id BIGINT,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    image_url VARCHAR(255),
    FOREIGN KEY (state_id) REFERENCES states(id) ON DELETE CASCADE
);




































CREATE TABLE traditional_wears (
    id BIGINT PRIMARY KEY,
    state_id BIGINT,
    men_wear VARCHAR(255),
    women_wear VARCHAR(255),
    image_url VARCHAR(255),
    FOREIGN KEY (state_id) REFERENCES states(id) ON DELETE CASCADE
);






































CREATE TABLE festivals (
    id BIGINT PRIMARY KEY,
    state_id BIGINT,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    image_url VARCHAR(255),
    FOREIGN KEY (state_id) REFERENCES states(id) ON DELETE CASCADE
);








































